chrome.runtime.onInstalled.addListener(()=>{chrome.tabs.create({url:"welcome.html"})});chrome.storage.session.setAccessLevel({accessLevel:"TRUSTED_AND_UNTRUSTED_CONTEXTS"});chrome.sidePanel.setPanelBehavior({openPanelOnActionClick:!0});chrome.runtime.onMessage.addListener((e,s)=>{let{tab:{id:t}}=s;chrome.storage.session.set({selectedTextInfo:e}),chrome.sidePanel.open({tabId:t})});
