document.addEventListener("dblclick",t=>{chrome.storage.session.get("pauseExtension").then(n=>{if(!(n!=null&&n.pauseExtension)){let e=window.getSelection().toString().trim().toLowerCase();if(g(e)){let{target:{textContent:i}}=t,s=l(e,i.split(".")),o=window.location.href;chrome.runtime.sendMessage({selectedText:e,sentence:s,currentURL:o,from:1})}}})});function l(t,n){let e;const i=n.filter(s=>new RegExp("\\b"+t+"\\b","i").test(s));return i.length>0&&(e=i[0]+".",e=e.replace(new RegExp("\\b"+t+"\\b","gi"),`<span>${t}</span>`)),e}function g(t){return/^[a-zA-Z]+$/.test(t)}
